import React, { useLayoutEffect,useState } from "react";
import { FlatList, Text, View, TouchableHighlight, Image,SafeAreaView, TextInput} from "react-native";
import styles from "./styles";
import Heart from '../../ui/icons/Heart';
import BackButton from "../../components/BackButton/BackButton";
// import { TextInput } from 'react-native-paper';
import SearchInput from '../../components/SearchInput/SearchInput';
export default function HomeScreen(props) {
  const [text, setText] = useState('');

  const { navigation } = props;

  const onPressRecipe = (item) => {
    navigation.navigate("Recipe", { item });
  };

  return (
    <View style={styles.container}>
      <View style={styles.toolbar}><Image style={styles.img} source={require("../../../assets/avatar.png")} />
      <BackButton /></View>
      <View style={styles.title}>
        <Text style={styles.title_text}>Привет, Динара!</Text>
      </View>
      <View style={styles.subtitle}>
        <Text style={styles.subtitle_text}>Что бронируем сегодня?</Text>
      </View>
      <SearchInput />
    </View>
  );
}
